import React, { Component } from 'react'
import "./Form.css"

export default class Form
    extends Component {
    constructor(props) {


        super(props)


        this.state = {
            isValid: false,
            userName: "",
            creaditCard: "",
            Date: "",
            Cvv: "",
            zipCode: "",
            Submit: false
        }


        this.handelSubmit = this.handelSubmit.bind(this);
    }

    




    handelSubmit() {
        this.setState({
            Submit: true
        })
    };



    render() {
        return (
            <>
                <div id="div-1" class="white_back">
                    <form action="" tabindex="" className="white_back">
                        <img id="avatar" className="white_back" src="./Assets/img/Happy Bunch Avatar.png" alt="user profile photo" />

                        <h3 id="payment_text" className="white_back">Payment info</h3>

                        <p className="white_back">Fullname</p>
                        <input type="text" maxlength="50" autofocus placeholder="Alex Smith" size="55" className="white_back , inp " />

                        <p className="white_back">Credit Card Number</p>
                        <input type="number" placeholder="1234 1234 1234 1234" className="white_back , inp " />

                        <div id="div2" className="white_back">
                            <p className="white_back">Exp Date</p>
                            <input type="date" placeholder="MM/YY" className="white_back , inpk" />
                        </div>

                        <div id="div3" className="white_back">
                            <p className="white_back">CVV</p>
                            <input type="password" placeholder="***" className="white_back , inpk" />
                        </div>

                        <p className="white_back">Zip Code</p>
                        <input type="number" placeholder="90210" className="white_back , inp" />

                        <button type="submit" id="buttom" onSubmit={this.handelSubmit}>Confirm Payment</button>
                        {this.state.Submit ? <p className="white_back submittag">You verify that info is correct</p> : <p className='submittag white_back'>ridi ke</p>}

                    </form>

                </div>
            </>
        )
    }
}
